CREATE DEFINER=`root`@`localhost` PROCEDURE `process_next_stages`()
BEGIN
    DECLARE done INT DEFAULT FALSE;
    DECLARE v_id INT;
    DECLARE v_student_id VARCHAR(50);
    DECLARE v_next_stage_type VARCHAR(50);

    -- 声明游标
    DECLARE cur CURSOR FOR
        SELECT id, student_id, next_stage_type
        FROM next_stage_queue
        WHERE processed = FALSE;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    -- 开启事务
    START TRANSACTION;

    OPEN cur;

    read_loop: LOOP
        FETCH cur INTO v_id, v_student_id, v_next_stage_type;
        IF done THEN
            LEAVE read_loop;
        END IF;

        -- 插入下一阶段记录
        INSERT INTO progress_records (student_id, stage_type, status)
        VALUES (v_student_id, v_next_stage_type, 'in_progress');

        -- 标记为已处理
        UPDATE next_stage_queue SET processed = TRUE WHERE id = v_id;
    END LOOP;

    CLOSE cur;

    -- 提交事务
    COMMIT;

    -- 清理已处理的旧记录（可选）
    DELETE FROM next_stage_queue
    WHERE processed = TRUE
    AND created_at < DATE_SUB(NOW(), INTERVAL 1 DAY);
END ;;
