/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `after_defense_score_complete` AFTER INSERT ON `defense_scores` FOR EACH ROW BEGIN
    DECLARE total_members INT;
    DECLARE scored_members INT;

    -- 获取已评分的委员数量
    SELECT COUNT(*) INTO scored_members
    FROM defense_scores
    WHERE arrangement_id = NEW.arrangement_id;

    -- 当所有委员都评分完成后，更新答辩状态为已完成
    IF scored_members = 5 THEN
        UPDATE defense_arrangements
        SET status = 'completed',
            updated_at = CURRENT_TIMESTAMP
        WHERE id = NEW.arrangement_id;
    END IF;
END */;;
