/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `after_task_book_record_insert` AFTER INSERT ON `progress_records` FOR EACH ROW BEGIN
    -- 只在插入任务书阶段记录时触发
    IF NEW.stage_type = 'task_book' THEN
        -- 从 topic_selections 表获取相关信息
        INSERT INTO task_assignments (
            teacher_id,
            student_id,
            topic_id,
            selection_id,
            title,
            content,
            requirements,
            deadline,
            status
        )
        SELECT
            t.teacher_id,           -- 从 topics 表获取教师ID
            ts.student_id,          -- 从 topic_selections 表获取学生ID
            ts.topic_id,            -- 从 topic_selections 表获取课题ID
            ts.selection_id,        -- 选题ID
            '毕业设计任务书',         -- 默认标题
            '请填写任务书内容',       -- 默认内容
            '请按要求完成任务书',     -- 默认要求
            NEW.deadline,           -- 使用进度记录中的截止时间
            'draft'                 -- 初始状态为草稿
        FROM topic_selections ts
        JOIN topics t ON ts.topic_id = t.topic_id
        WHERE ts.student_id = NEW.student_id
        AND ts.final_status = 'approved'
        LIMIT 1;
    END IF;
END */;;
