/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `after_peer_review_complete` AFTER UPDATE ON `progress_records` FOR EACH ROW BEGIN
    -- 声明变量用于调试
    DECLARE debug_msg TEXT;

    -- 记录触发器被调用的情况
    INSERT INTO system_logs (
        action,
        details
    ) VALUES (
        'trigger.peer_review.debug',
        CONCAT(
            'Trigger called with: ',
            'stage_type=', NEW.stage_type,
            ', old_status=', OLD.status,
            ', new_status=', NEW.status,
            ', student_id=', NEW.student_id
        )
    );

    -- 检查是否是同行评议阶段且状态变为已完成
    IF NEW.stage_type = 'peer_review'
    AND NEW.status = 'completed'
    AND OLD.status != 'completed' THEN

        -- 记录条件匹配的情况
        INSERT INTO system_logs (
            action,
            details
        ) VALUES (
            'trigger.peer_review.match',
            CONCAT('Conditions matched for student_id: ', NEW.student_id)
        );

        -- 更新对应的论文提交记录状态
        UPDATE thesis_submissions
        SET status = 'completed',
            updated_at = CURRENT_TIMESTAMP
        WHERE student_id = NEW.student_id
        AND status IN ('reviewing', 'review_assigned', 'submitted') -- 放宽状态检查条件
        ORDER BY version DESC
        LIMIT 1;

        -- 记录更新操作的影响行数
        SET debug_msg = CONCAT(
            'Update completed. Rows affected: ', ROW_COUNT(),
            '. Student ID: ', NEW.student_id
        );

        INSERT INTO system_logs (
            action,
            details
        ) VALUES (
            'trigger.peer_review.update',
            debug_msg
        );
    END IF;
END */;;
