/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `sync_translation_progress` AFTER UPDATE ON `progress_records` FOR EACH ROW BEGIN
    -- 只处理外文翻译阶段的更新
    IF NEW.stage_type = 'translation' THEN
        -- 检查是否已存在该学生的外文翻译记录
        IF EXISTS (SELECT 1 FROM translation_reviews WHERE student_id = NEW.student_id) THEN
            -- 如果存在则更新
            UPDATE translation_reviews
            SET file_url = CASE
                    -- 只在文件URL发生变化时更新
                    WHEN NEW.file_url != OLD.file_url THEN NEW.file_url
                    ELSE file_url
                END,
                status = CASE
                    -- 只在状态发生变化时更新
                    WHEN NEW.status != OLD.status THEN
                        CASE NEW.status
                            WHEN 'not_started' THEN 'pending'
                            WHEN 'in_progress' THEN 'pending'
                            WHEN 'reviewing' THEN 'reviewing'
                            WHEN 'revision_needed' THEN 'revision_needed'
