                    WHEN NEW.submit_time != OLD.submit_time THEN NEW.submit_time
                    ELSE submit_time
                END,
                updated_at = CURRENT_TIMESTAMP
            WHERE student_id = NEW.student_id
            ORDER BY version DESC
            LIMIT 1;
        ELSE
            -- 如果不存在则插入新记录
            INSERT INTO translation_reviews (
                student_id,
