/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `sync_proposal_progress` AFTER UPDATE ON `progress_records` FOR EACH ROW BEGIN
    DECLARE v_teacher_id VARCHAR(50);

    -- 只处理开题报告阶段的更新
    IF NEW.stage_type = 'proposal' THEN
        -- 检查是否已存在该学生的开题报告记录
        IF EXISTS (SELECT 1 FROM proposal_reviews WHERE student_id = NEW.student_id) THEN
            -- 如果存在则更新
            UPDATE proposal_reviews
            SET file_url = CASE
                    -- 使用 COALESCE 处理 NULL 值的比较
                    WHEN COALESCE(NEW.file_url, '') != COALESCE(OLD.file_url, '') THEN NEW.file_url
                    ELSE file_url
                END,
                status = CASE
                    WHEN COALESCE(NEW.status, '') != COALESCE(OLD.status, '') THEN NEW.status
                    ELSE status
                END,
                submit_time = CASE
                    WHEN COALESCE(NEW.submit_time, '') != COALESCE(OLD.submit_time, '') THEN NEW.submit_time
                    ELSE submit_time
                END,
                updated_at = CURRENT_TIMESTAMP
            WHERE student_id = NEW.student_id
            ORDER BY version DESC
            LIMIT 1;

            -- 添加调试日志（如果有日志表的话）
            /*
            INSERT INTO debug_logs (message)
            VALUES (CONCAT('Updated proposal_reviews for student: ', NEW.student_id,
                         ', Old file_url: ', COALESCE(OLD.file_url, 'NULL'),
                         ', New file_url: ', COALESCE(NEW.file_url, 'NULL')));
            */

        ELSE
            -- 如果不存在则获取教师ID并插入新记录
            SELECT t.teacher_id INTO v_teacher_id
            FROM topic_selections ts
            JOIN topics t ON ts.topic_id = t.topic_id
            WHERE ts.student_id = NEW.student_id
            AND ts.final_status = 'approved'
            LIMIT 1;

            -- 插入新记录
            IF v_teacher_id IS NOT NULL THEN
                INSERT INTO proposal_reviews (
                    student_id,
                    teacher_id,
                    version,
                    file_url,
                    status,
                    submit_time,
                    created_at,
                    updated_at
                ) VALUES (
                    NEW.student_id,
                    v_teacher_id,
                    1,
                    NEW.file_url,
                    NEW.status,
                    NEW.submit_time,
                    CURRENT_TIMESTAMP,
                    CURRENT_TIMESTAMP
                );
            END IF;
        END IF;
    END IF;
END */;;
