CREATE DEFINER=`root`@`localhost` PROCEDURE `process_progress_queue`()
BEGIN
    DECLARE done INT DEFAULT FALSE;
    DECLARE v_student_id VARCHAR(50);
    DECLARE v_next_stage_type VARCHAR(50);
    DECLARE cur CURSOR FOR
        SELECT student_id, next_stage_type
        FROM progress_stage_queue
        ORDER BY created_at;
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    OPEN cur;
    read_loop: LOOP
        FETCH cur INTO v_student_id, v_next_stage_type;
        IF done THEN
            LEAVE read_loop;
        END IF;

        INSERT INTO progress_records (student_id, stage_type, status, created_at)
        VALUES (v_student_id, v_next_stage_type, 'in_progress', NOW())
        ON DUPLICATE KEY UPDATE
            status = 'in_progress',
            updated_at = NOW();

        DELETE FROM progress_stage_queue
        WHERE student_id = v_student_id
        AND next_stage_type = v_next_stage_type;
    END LOOP;
    CLOSE cur;
END ;;
