CREATE DEFINER=`root`@`localhost` PROCEDURE `sync_thesis_submission`(
    IN p_student_id VARCHAR(50),
    IN p_stage_type VARCHAR(20),
    IN p_status VARCHAR(20),
    IN p_file_url VARCHAR(255),
    IN p_submit_time DATETIME,
    IN p_review_time DATETIME,
    IN p_teacher_comment TEXT,
    IN p_is_update BOOLEAN,
    IN p_old_status VARCHAR(20),
    IN p_old_file_url VARCHAR(255)
)
BEGIN
    DECLARE v_advisor_id VARCHAR(20);

    -- 只处理论文提交阶段的记录
    IF p_stage_type = 'thesis_submit' THEN
        -- 获取指导教师ID
        SELECT t.teacher_id INTO v_advisor_id
        FROM topic_selections ts
        JOIN topics t ON ts.topic_id = t.topic_id
        WHERE ts.student_id = p_student_id
        AND ts.final_status = 'approved'
        LIMIT 1;

        -- 检查是否已存在该学生的论文提交记录
        IF EXISTS (SELECT 1 FROM thesis_submissions WHERE student_id = p_student_id) THEN
            -- 如果存在则更新
            UPDATE thesis_submissions
            SET
                file_url = CASE
                    WHEN p_file_url IS NOT NULL AND (
                        p_is_update = FALSE OR
                        (p_is_update = TRUE AND p_file_url != p_old_file_url)
                    ) THEN p_file_url
                    ELSE file_url
                END,
                version = CASE
                    WHEN p_is_update = FALSE OR
                         (p_file_url != p_old_file_url OR p_status != p_old_status)
                    THEN version + 1
                    ELSE version
                END,
                status = CASE
                    WHEN p_is_update = FALSE OR p_status != p_old_status THEN
                        CASE p_status
                            WHEN 'reviewing' THEN 'advisor_reviewing'
                            WHEN 'revision_needed' THEN 'advisor_rejected'
                            WHEN 'completed' THEN 'advisor_approved'
                            WHEN 'in_progress' THEN 'submitted'
                            ELSE 'submitted'
                        END
                    ELSE status
                END,
                advisor_review_status = CASE
                    WHEN p_is_update = FALSE OR p_status != p_old_status THEN
                        CASE p_status
                            WHEN 'completed' THEN 'approved'
                            WHEN 'reviewing' THEN 'reviewing'
                            WHEN 'revision_needed' THEN 'rejected'
                            ELSE advisor_review_status
                        END
                    ELSE advisor_review_status
                END,
                advisor_review_time = CASE
                    WHEN p_is_update = FALSE OR p_review_time != advisor_review_time
                    THEN p_review_time
                    ELSE advisor_review_time
                END,
                advisor_review_comments = CASE
                    WHEN p_is_update = FALSE OR p_teacher_comment != advisor_review_comments
                    THEN p_teacher_comment
                    ELSE advisor_review_comments
                END,
                submit_time = CASE
                    WHEN p_is_update = FALSE OR p_submit_time != submit_time
                    THEN p_submit_time
                    ELSE submit_time
                END,
                advisor_id = COALESCE(advisor_id, v_advisor_id),
                updated_at = CURRENT_TIMESTAMP
            WHERE student_id = p_student_id;
        ELSE
            -- 插入新记录
            INSERT INTO thesis_submissions (
                student_id,
                advisor_id,
                version,
                file_url,
                title,
                status,
                advisor_review_status,
                advisor_review_time,
                advisor_review_comments,
                submit_time,
                created_at,
                updated_at
            ) VALUES (
                p_student_id,
                v_advisor_id,
                1,
                p_file_url,
                '论文题目待更新',
                CASE p_status
                    WHEN 'reviewing' THEN 'advisor_reviewing'
                    WHEN 'revision_needed' THEN 'advisor_rejected'
                    WHEN 'completed' THEN 'advisor_approved'
                    WHEN 'in_progress' THEN 'submitted'
                    ELSE 'submitted'
                END,
                CASE p_status
                    WHEN 'completed' THEN 'approved'
                    WHEN 'reviewing' THEN 'reviewing'
                    WHEN 'revision_needed' THEN 'rejected'
                    ELSE 'pending'
                END,
                p_review_time,
                p_teacher_comment,
                COALESCE(p_submit_time, CURRENT_TIMESTAMP),
                CURRENT_TIMESTAMP,
                CURRENT_TIMESTAMP
            );
        END IF;
    END IF;
END ;;
